import React, { useState } from "react";
import "./Login.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function LoginPage() {
  return (
    <div className="loginPageContainer">
      <Heading />
      <LoginBox />
      <FoodPhotos />
    </div>
  );
}

function Heading() {
  return (
    <div id="heading">
      <p>
        Hostel
        <br />
        <p id="foodManagement">Food Management System</p>
      </p>
    </div>
  );
}

function LoginBox() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check for empty fields
    if (!username || !password || !userType) {
      setErrorMessage("Please fill in all fields and select a user type.");
      return;
    }

    setErrorMessage("");

    try {
      // Send POST request to login endpoint
      const response = await axios.post("http://localhost:5000/api/login", {
        username,
        password,
        userType,
      });

      if (response.status === 200) {
        // Redirect based on user type
        switch (userType) {
          case "hostel_resident":
            navigate("/resident_home");
            break;
          case "warden":
            navigate("/warden/home_page/homePage.js");
            break;
          case "kitchen_department":
            navigate("/kitchen/home_page/homePage.js");
            break;
          case "admin":
            navigate("/admin/home_page/homePage.js");
            break;
          default:
            setErrorMessage("Invalid user type selected.");
        }
      }
    } catch (error) {
      // Display an error message if login fails
      setErrorMessage(
        error.response?.data?.message || "Login failed. Please try again."
      );
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <select value={userType} onChange={(e) => setUserType(e.target.value)}>
          <option value="">Select user type</option>
          <option value="hostel_resident">Hostel Resident</option>
          <option value="warden">Warden</option>
          <option value="kitchen_department">Kitchen Department</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">Login</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
}

function FoodPhotos() {
  return (
    <div id="login_page_Photos_Container">
      <div id="buildings">
        <div id="building"></div>
        <div id="Old_built"></div>
      </div>
      <div id="Food"></div>
    </div>
  );
}

export default LoginPage;
