import React, { useState, useRef, useEffect } from "react";
import "./FoodQR.css";
import { Link } from "react-router-dom";
import { QRCodeCanvas } from "qrcode.react";

function NavbarBG() {
  return <div id="navbarbg"></div>;
}

function NavbarItems2() {
  return (
    <div id="NI2">
      <h1 id="hr">HOSTEL RESIDENT</h1>
      <Link to="/resident_home" id="h">
        Home
      </Link>
      <Link to="/meal-selection" id="mealselect">
        Meal Selection
      </Link>
      <a href="#" id="com">
        Complaints
      </a>
      <Link to="/residentprofile" id="pro">
        Profile
      </Link>
      <Link to="/" id="log">
        Logout
      </Link>
    </div>
  );
}

function FoodQRHeading() {
  return <h1 id="FoodQRGrad">Food QR</h1>;
}

function WeekButtonsQR({ setDay }) {
  const days = [
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
    "Sunday",
  ];
  return (
    <div id="weekbtns">
      {days.map((day) => (
        <button
          key={day}
          id={day.toLowerCase().slice(0, 3)}
          onClick={() => setDay(day)}
        >
          {day}
        </button>
      ))}
    </div>
  );
}

function QRDownload({ qrContent }) {
  const qrRef = useRef();

  const downloadQR = () => {
    const qrCanvas = qrRef.current.querySelector("canvas");
    const qrURL = qrCanvas.toDataURL("image/png");
    const downloadLink = document.createElement("a");
    downloadLink.href = qrURL;
    downloadLink.download = "QR_Code.png";
    downloadLink.click();
  };

  return (
    <div id="QRDownBG">
      <div id="ActualQR" ref={qrRef}>
        {qrContent ? (
          <QRCodeCanvas value={qrContent} size={300} />
        ) : (
          <p>No meal selected yet</p>
        )}
      </div>
      {qrContent && (
        <button id="QRDownbtn" onClick={downloadQR}>
          Download
        </button>
      )}
    </div>
  );
}

function FoodQR() {
  const [selectedDay, setSelectedDay] = useState("Monday");
  const [qrContent, setQrContent] = useState(null);
  const residentId = localStorage.getItem("residentId");
  const residentName = localStorage.getItem("residentname");

  // Function to get current week ID
  const getCurrentWeekID = () => {
    const today = new Date();
    const year = today.getFullYear();
    const weekNumber = Math.ceil((today.getDate() - today.getDay() + 10) / 7);
    return `${year}-${weekNumber}`;
  };

  useEffect(() => {
    const mealSelectionsKey = `mealSelections_${residentId}`;
    const mealSelections =
      JSON.parse(localStorage.getItem(mealSelectionsKey)) || {};
    const selectedMeal = mealSelections[selectedDay];
    const weekID = getCurrentWeekID();

    if (selectedMeal && selectedMeal.selection === "Yes I will Eat") {
      const qrData = `${weekID}_${residentId}_${residentName}`;
      setQrContent(qrData);
    } else {
      setQrContent(null);
    }
  }, [selectedDay, residentId, residentName]);

  // Week color scheme
  const getWeekColor = () => {
    const colors = ["#FFB6C1", "#ADD8E6", "#90EE90", "#FFFFE0"]; // Example colors
    const weekNumber =
      parseInt(getCurrentWeekID().split("-")[1], 10) % colors.length;
    return colors[weekNumber];
  };

  return (
    <div id="foodQRContainer" style={{ backgroundColor: getWeekColor() }}>
      <NavbarBG />
      <NavbarItems2 />
      <FoodQRHeading />
      <WeekButtonsQR setDay={setSelectedDay} />
      {qrContent ? (
        <QRDownload qrContent={qrContent} />
      ) : (
        <p>No meal booked for {selectedDay}</p>
      )}
    </div>
  );
}
export default FoodQR;
