import React, { useState, useEffect } from "react";
import "./ResProfile.css";
import { Link } from "react-router-dom";
import axios from "axios";

function NavbarBG() {
  return <div id="navbarbg"></div>;
}

function NavbarItems2() {
  return (
    <div id="NI2">
      <h1 id="prohr">HOSTEL RESIDENT</h1>
      <Link to="/resident_home" id="proh">
        Home
      </Link>
      <Link to="/foodqr" id="fqr">
        Food QR
      </Link>
      <Link to="/meal-selection" id="mealselection">
        Meal Selection
      </Link>
      <Link to="/" id="log">
        Logout
      </Link>
    </div>
  );
}

function ProfileHeading() {
  return <h1 id="ProfileGrad">Profile</h1>;
}

function ProfileBoxBG() {
  const [resident, setResident] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchResidentData = async () => {
      const residentId = localStorage.getItem("residentId");
      try {
        const response = await axios.get(
          `http://localhost:5000/api/profileMealSelection/${residentId}`
        );

        setResident(response.data);
      } catch (error) {
        console.error("Error fetching resident data:", error);
        setError("Failed to load resident details.");
      } finally {
        setLoading(false);
      }
    };

    fetchResidentData();
  }, []);

  return (
    <div id="ProfileBoxBg">
      <div id="ProfilePhotoWrapper">
        <img
          id="ProfilePhoto"
          src="/FoodIcons/Profile.jpg"
          alt="Profile Photo"
        />
      </div>
      <div id="ProfileDetails">
        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p>{error}</p>
        ) : resident ? (
          <>
            <p id="ResidentName">
              <strong>Name:</strong>{" "}
              <span style={{ fontWeight: "normal" }}>
                {resident.residentname}
              </span>
            </p>
            <p id="ResidentEmailID">
              <strong>Email ID:</strong>{" "}
              <span style={{ fontWeight: "normal" }}>{resident.username}</span>
            </p>
            <p id="ResidentID">
              <strong>Resident ID:</strong>{" "}
              <span style={{ fontWeight: "normal" }}>
                {resident.residentID}
              </span>
            </p>
            <p id="PhoneNo">
              <strong>Phone No:</strong>{" "}
              <span style={{ fontWeight: "normal" }}>{resident.phoneNo}</span>
            </p>
          </>
        ) : (
          <p>No resident details available.</p>
        )}
      </div>
    </div>
  );
}

function BookingsHeading() {
  return <h1 id="BookingsHeading">Your Bookings</h1>;
}

function WeekButtonsInProfile({ onWeekChange }) {
  return (
    <div id="BtnsBGProfileWrapper">
      <div id="BtnsBGProfile">
        {[
          "Monday",
          "Tuesday",
          "Wednesday",
          "Thursday",
          "Friday",
          "Saturday",
          "Sunday",
        ].map((day, index) => (
          <button
            key={index}
            id={`${day.toLowerCase()}pro`}
            onClick={() => onWeekChange(day)}
          >
            {day}
          </button>
        ))}
      </div>
    </div>
  );
}

function BookingsDetailsInProfile({ bookedDetails }) {
  return (
    <div id="BookingDetailsBGWrapper">
      <div id="BookingDetailsBG">
        {bookedDetails.length > 0 ? (
          bookedDetails.map((meal, index) => (
            <div key={index}>
              <div id="breakfastProfie">
                <p id="breakHead">Breakfast</p>
                <p id="breakfastDetails">{meal.breakfastDish}</p>
              </div>
              <div id="lunchProfie">
                <p id="lunchHead">Lunch</p>
                <p id="lunchDetails">{meal.lunchDish}</p>
              </div>
              <div id="DinnerProfile">
                <p id="dinnerHead">Dinner</p>
                <p id="dinnerDetails">{meal.dinnerDish}</p>
              </div>
            </div>
          ))
        ) : (
          <p>No booked meal details available.</p>
        )}
      </div>
    </div>
  );
}

function ResProfile() {
  const [bookedDetails, setBookedDetails] = useState([]);
  const [loadingBookings, setLoadingBookings] = useState(true);
  const [errorBookings, setErrorBookings] = useState(null);

  const handleWeekChange = async (day) => {
    const residentId = localStorage.getItem("residentId");
    try {
      const response = await axios.get(
        `http://localhost:5000/api/meals/booked/${residentId}/${day}`
      );
      setBookedDetails(response.data);
    } catch (error) {
      console.error("Error fetching booked meal details:", error);
      setErrorBookings("Failed to load booked meal details.");
    } finally {
      setLoadingBookings(false);
    }
  };

  useEffect(() => {
    handleWeekChange("Monday"); // Fetch details for Monday by default
  }, []);

  return (
    <div id="resprocontainer">
      <NavbarBG />
      <NavbarItems2 />
      <ProfileHeading />
      <ProfileBoxBG />
      <BookingsHeading />

      <WeekButtonsInProfile onWeekChange={handleWeekChange} />

      {loadingBookings ? (
        <p>Loading booked details...</p>
      ) : errorBookings ? (
        <p>{errorBookings}</p>
      ) : (
        <BookingsDetailsInProfile bookedDetails={bookedDetails} />
      )}
    </div>
  );
}

export default ResProfile;
