import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ResProfile.css";

function ResProfile() {
  const [userDetails, setUserDetails] = useState({});
  const [mealSelections, setMealSelections] = useState([]);
  const [selectedDay, setSelectedDay] = useState(null);

  useEffect(() => {
    // Fetch user details and meal selections based on residentId
    const residentId = "HR01"; // Replace this with actual residentId
    axios
      .get(`/mealselection/${residentId}`)
      .then((response) => setMealSelections(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleDayClick = (day) => {
    setSelectedDay(day);
  };

  const renderDaySelection = () => {
    const selectedMeal = mealSelections.find(
      (meal) => meal.bookedDay === selectedDay
    );
    return selectedMeal ? (
      <div id="BookingDetailsBG">
        <div id="breakfastProfile">
          <p>Breakfast</p>
          <p>{selectedMeal.breakfastDish || "Not selected"}</p>
        </div>
        <div id="lunchProfile">
          <p>Lunch</p>
          <p>{selectedMeal.lunchDish || "Not selected"}</p>
        </div>
        <div id="dinnerProfile">
          <p>Dinner</p>
          <p>{selectedMeal.dinnerDish || "Not selected"}</p>
        </div>
      </div>
    ) : (
      <p>No selection for {selectedDay}</p>
    );
  };

  return (
    <div id="resprocontainer">
      <h1>Profile</h1>
      {/* Profile and navigation components */}

      <h2>Meal Selections</h2>
      <div id="BtnsBGProfile">
        {[
          "Monday",
          "Tuesday",
          "Wednesday",
          "Thursday",
          "Friday",
          "Saturday",
          "Sunday",
        ].map((day) => (
          <button key={day} onClick={() => handleDayClick(day)}>
            {day}
          </button>
        ))}
      </div>

      <div>{selectedDay && renderDaySelection()}</div>
    </div>
  );
}

export default ResProfile;
