import React, { useState, useRef, useEffect } from "react";
import "./FoodQR.css";
import { Link } from "react-router-dom";
import { QRCodeCanvas } from "qrcode.react";

function NavbarBG() {
  return <div id="navbarbg"></div>;
}

function NavbarItems2() {
  return (
    <div id="NI2">
      <h1 id="hr">HOSTEL RESIDENT</h1>
      <Link to="/resident_home" id="h">
        Home
      </Link>
      <Link to="/meal-selection" id="mealselect">
        Meal Selection
      </Link>
      <a href="#" id="com">
        Complaints
      </a>
      <Link to="/residentprofile" id="pro">
        Profile
      </Link>
      <Link to="/" id="log">
        Logout
      </Link>
    </div>
  );
}

function FoodQRHeading() {
  return <h1 id="FoodQRGrad">Food QR</h1>;
}

function QRDownload({ qrContent }) {
  const qrRef = useRef();

  const downloadQR = () => {
    const qrCanvas = qrRef.current.querySelector("canvas");
    const qrURL = qrCanvas.toDataURL("image/png");
    const downloadLink = document.createElement("a");
    downloadLink.href = qrURL;
    downloadLink.download = "QR_Code.png";
    downloadLink.click();
  };

  return (
    <div id="QRDownBG">
      <div id="ActualQR" ref={qrRef}>
        {qrContent ? (
          <QRCodeCanvas value={qrContent} size={300} />
        ) : (
          <p style={{ textAlign: "center", fontSize: "18px", color: "#888" }}>
            No meal selected yet
          </p>
        )}
      </div>
      {qrContent && (
        <button id="QRDownbtn" onClick={downloadQR}>
          Download
        </button>
      )}
    </div>
  );
}

function FoodQR() {
  const [qrContent, setQrContent] = useState(null);
  const residentId = localStorage.getItem("residentId");
  const residentName = localStorage.getItem("residentname");

  useEffect(() => {
    // const today = new Date();
    const today = "2/11/2024";
    const options = {
      weekday: "long",
      year: "numeric",
      month: "numeric",
      day: "numeric",
    };
    const formattedDate = today.toLocaleDateString("en-GB", options); // Format: DD/MM/YYYY

    // Fetch meal booking status for today
    const mealSelectionsKey = `mealSelections_${residentId}`;
    const mealSelections =
      JSON.parse(localStorage.getItem(mealSelectionsKey)) || {};
    const selectedMeal = mealSelections[formattedDate];

    if (selectedMeal && selectedMeal.selection === "Yes I will Eat") {
      const qrData = `${formattedDate}_${residentId}_${residentName}`;
      setQrContent(qrData);
    } else {
      setQrContent(null); // No QR if the meal isnâ€™t booked
    }
  }, [residentId, residentName]);

  return (
    <div id="foodQRContainer">
      <NavbarBG />
      <NavbarItems2 />
      <FoodQRHeading />
      <QRDownload qrContent={qrContent} />
    </div>
  );
}

export default FoodQR;
